        props.handleClick(task)
        setTask('')
    }
    const handleChange = (event) => {
        setTask(event.target.value)
    }

    return (
        <div>
            <input name="newTask" onChange={handleChange} value={task}/>
            <input type="submit" onClick={submit} />
        </div>
    )
}

